import java.nio.charset.StandardCharsets
import org.apache.nifi.controller.ControllerService
import groovy.sql.Sql

def  ff = session.get()
if(!ff)return

//  create connection via connection pool services

def lookup = context.controllerServiceLookup 

def dbcpServiceId = lookup.getControllerServiceIdentifiers(ControllerService).find
{ cs -> lookup.getControllerServiceName(cs) == 'DBCPConnectionPool_GP_prj_gistek_preprod' }

def conn = lookup.getControllerService(dbcpServiceId)?.getConnection()
def sql = new Sql(conn)
def mcode  = ff.getAttribute('mcode')
def version= ff.getAttribute('version')


// Updating other rows with certain mcode by giving version column proper value instead of  null

sql.executeUpdate "UPDATE publish.stg_templates SET version =   TO_TIMESTAMP(${version}, 'DD.MM.YYYY HH.MI.SS') where mcode = ${mcode}"



conn?.close()
session.transfer(ff, REL_SUCCESS)
session.commit()

